/**
 * Get the full dataset name with environment suffix.
 *
 * @param base - Base dataset name (e.g., "sandbox-telemetry-system")
 * @returns Full name with suffix (e.g., "vm0-sandbox-telemetry-system-dev")
 */
export function getDatasetName(base: string): string {
  const isProd = process.env.VERCEL_ENV === "production";
  return `vm0-${base}-${isProd ? "prod" : "dev"}`;
}

/**
 * Pre-defined dataset bases for type safety.
 * Full names are generated by getDatasetName() with environment suffix.
 */
export const DATASETS = {
  SANDBOX_TELEMETRY_SYSTEM: "sandbox-telemetry-system",
  SANDBOX_TELEMETRY_METRICS: "sandbox-telemetry-metrics",
  SANDBOX_TELEMETRY_NETWORK: "sandbox-telemetry-network",
  AGENT_RUN_EVENTS: "agent-run-events",
  WEB_LOGS: "web-logs",
  REQUEST_LOG: "request-log",
  SANDBOX_OP_LOG: "sandbox-op-log",
} as const;
